import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

function openAppInNewTab(Component) {
  const newTab = window.open('dada', '_blank');
  newTab.document.write(`
    <html>
      <head>
        <title>Новая вкладка</title>
      </head>
      <body>
        <div id="root"></div>
        <script>
          window.onload = function() {
            ReactDOM.render(
              React.createElement(${Component.name}),
              document.getElementById('root')
            );
          };
        </script>
      </body>
    </html>
  `);
}

function RenderAppButton() {
  return (
    <>
      <button onClick={() => openAppInNewTab(App)}>Открыть приложение 1 в новой вкладке</button>
      <button onClick={() => openAppInNewTab(App)}>Открыть приложение 2 в новой вкладке</button>
    </>
  );
}

ReactDOM.createRoot(document.getElementById('root')).render(<RenderAppButton />);
reportWebVitals();
