import './App.css';
import React, { useState } from 'react'
import Chess from './lab01/Chess.js';
import Table from './lab01/table.js';
import Calendar from './lab01/Calendar.js';
import Clock from './laba2/Clock.js';
import JobMenu from './laba2/JobMenu';

function App() {
  const [isVisible1, setIsVisible1] = useState(false);
  const [isVisible2, setIsVisible2] = useState(false);

  const handleButtonClick1 = () => {
    setIsVisible1(!isVisible1);
  }
  const handleButtonClick2 = () => {
    setIsVisible2(!isVisible2);
  }
  return (
    <>
      <div>
        <button onClick={handleButtonClick1}>Лаба 1</button>
        {isVisible1 && (
          <div>
            <h1>1 задание</h1>
            <Calendar />
            <h1>2 задание</h1>
            <Table />
            <h1>3 задание</h1>
            <Chess />
          </div>
        )}
      </div>
      <div>
        <button onClick={handleButtonClick2}>лаба 2</button>
        {isVisible2 && (
          <div>
            <h1>1 задание</h1>
            <Clock format='24' timezone='+3:00' />
            <Clock format='12' timezone='-5:00' />
            <h1>2 задание</h1>
            <JobMenu />
          </div>
        )
        }
      </div>
    </>

  );

}

export default App;
